import React, { useState } from 'react';
import './App.css';

import Buttons from './Buttons';
import Screen from './Screen';

function App() {
  const [atual, setAtual] = useState('0');
  const [anterior, setAnterior] = useState('');

  function atualizarValor(valorAtual){
    if(valorAtual === 'AC'){
      setAnterior('');
      setAtual('0');
      return;
    }

    if(!isNaN(valorAtual)){      
      if(atual.length < 8){           
        if (atual === '0'){
          setAtual(valorAtual);
        }else{
          setAtual(atual+valorAtual)
        }
      }
    }else{
      switch(valorAtual){
        case 'C':
          setAtual('0');
          break;
        case '.':
          if(atual.length < 7){
            setAtual(atual+valorAtual);
          }
          break;
        case '+':
          if(anterior !== ''){
            setAnterior(atual);
            setAtual(parseInt(atual)+parseInt(anterior));           
          }
          break;
        case '-':
          if (anterior !== '') {
            setAnterior(atual);
            setAtual(parseInt(atual) - parseInt(anterior));
          }
          break;
        case '/':
          if (anterior !== '') {
            setAnterior(atual);
            setAtual(parseInt(atual) / parseInt(anterior));
          }
          break;
        case 'X':
          if (anterior !== '') {
            setAnterior(atual);
            setAtual(parseInt(atual) * parseInt(anterior));
          }
          break;

      }           
    }  
  }

  return (
    <div className="App">
      <div className="borda-exterior">
        <div className="borda-inferior">
          <div className="tela">
            <Screen valores={{atual, anterior}}/>
          </div>
          <div className="botoes">
            <Buttons atualizarValor={atualizarValor}/>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
