import React from 'react';
import './App.css';

import Buttons from './Buttons';
import Screen from './Screen';

function App() {
  const [atualValor, setAtual] = useState('0');

  function atualizarValor(valorAtual){
      setAtual(valorAtual);
  }

  return (
    <div className="App">
      <div className="borda-exterior">
        <div className="borda-inferior">
          <div className="tela">
            <Screen atual={atualizarValor}/>
          </div>
          <Buttons />
        </div>
      </div>
    </div>
  );
}

export default App;
