import React, { useState } from 'react';
import './App.css';

import Buttons from './Buttons';
import Screen from './Screen';

function App() {
  const [atual, setAtual] = useState('');
  const [anterior, setAnterior] = useState('');
  const [operacao, setOperacao] = useState('');

  let on = false;
  let light = '#10710C';

  function luz(blight){
    if(blight){
      light = '#5DFF00';
    }else{
      luz = '#10710C';
    }
  }

  function atualizarValor(valorAtual){
    if(valorAtual === 'ON'){        
        if(on){
          setAtual('');
          luz(false);
          return;
        }else{
          setAtual('0');
          luz(true);
          return;
        }
    }

      switch (valorAtual) {
        case 'AC':
          setAnterior('');
          setAtual('0');
          setOperacao('');
          break;
        case 'C':
          setAtual('0');
          break;
        case '.':
          if (atual.length < 7) {
            setAtual(atual + valorAtual);
          }
          break;
        case '+':
          if (anterior !== '') {
            setAnterior(atual);
            setAtual(parseInt(atual) + parseInt(anterior));
          }else{
            setAnterior(atual);
            setAtual('0');
            setOperacao('+');
          }
          break;
        case '-':
          if (anterior !== '') {
            setAnterior(atual);
            setAtual(parseInt(atual) - parseInt(anterior));
          }else{
            setAnterior(atual);
            setAtual('0');
            setOperacao('-');
          }
          break;
        case '/':
          if (anterior !== '') {
            setAnterior(atual);
            setAtual(parseInt(atual) / parseInt(anterior));
          }else{
            setAnterior(atual);
            setAtual('0');
            setOperacao('/');
          }
          break;
        case 'X':
          if (anterior !== '') {
            setAnterior(atual);
            setAtual(parseInt(atual) * parseInt(anterior));
          }else{
            setAnterior(atual);
            setAtual('0');
            setOperacao('X');
          }
          break;
        case '=':
          if (anterior !== '') {
            if(operacao === '+'){
              setAnterior(atual);
              setAtual(Number(anterior)+Number(atual));
            }
            if(operacao === '-'){
              setAnterior(atual);
              setAtual(Number(anterior)-Number(atual));
            }
            if(operacao === '/'){
              setAnterior(atual);
              setAtual(Number(anterior)/Number(atual));
            }
            if(operacao === 'X'){
              setAnterior(atual);
              setAtual(Number(anterior)*Number(atual));
            }
            setAnterior('');
            setOperacao('');
          }else{
            setAtual('ERR');         
          }        
          break;
        case 'S':
          if(atual !== '0'){
            if(Number(atual) > 0)
              setAtual('-'+atual);
            else
              setAtual(Math.abs(Number(atual)));
          }
          break;
        default:        
          if(atual.length < 8) {          
            if (atual === '0' || atual === 'ERR') {
              setAtual(valorAtual);
            } else {
              setAtual(atual + valorAtual)
            }
          }
      } 
  }

  return (
    <div className="App">
      <div className="borda-exterior">
        <div className="borda-inferior" style={{ backgroundColor: luz}}>
          <div className="tela">
            <Screen valores={{atual, anterior, operacao}}/>
          </div>
          <div className="botoes">
            <Buttons atualizarValor={atualizarValor}/>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
