import React, { useState } from 'react';
import './App.css';

import Buttons from './Buttons';
import Screen from './Screen';

function App() {
  const [atual, setAtual] = useState('0');
  const [anterior, setAnterior] = useState('');
  const [operacao, setOperacao] = useState('');

  function atualizarValor(valorAtual){
    switch (valorAtual) {
      case 'AC':
        setAnterior('');
        setAtual('0');
        break;
      case 'C':
        setAtual('0');
        break;
      case '.':
        if (atual.length < 7) {
          setAtual(atual + valorAtual);
        }
        break;
      case '+':
        if (anterior !== '') {
          setAnterior(atual);
          setAtual(parseInt(atual) + parseInt(anterior));
        }else{
          setAnterior(atual);
          setAtual('0');
          setOperacao('+');
        }
        break;
      case '-':
        if (anterior !== '') {
          setAnterior(atual);
          setAtual(parseInt(atual) - parseInt(anterior));
        }else{
          setAnterior(atual);
          setAtual('0');
          setOperacao('-');
        }
        break;
      case '/':
        if (anterior !== '') {
          setAnterior(atual);
          setAtual(parseInt(atual) / parseInt(anterior));
        }else{
          setAnterior(atual);
          setAtual('0');
          setOperacao('/');
        }
        break;
      case 'X':
        if (anterior !== '') {
          setAnterior(atual);
          setAtual(parseInt(atual) * parseInt(anterior));
        }else{
          setAnterior(atual);
          setAtual('0');
          setOperacao('X');
        }
        break;
      case '=':
        if (anterior !== '') {
          setAnterior(atual);
          setAtual(parseInt(atual)+parseInt(anterior));
        }else{
          setAtual('ERR');         
        }        
        break;
      default:
        if(atual == 'ERR'){
          setAtual('');
        }
        if (atual.length < 8) {
          if (atual === '0') {
            setAtual(valorAtual);
          } else {
            setAtual(atual + valorAtual)
          }
        }
    } 
  }

  return (
    <div className="App">
      <div className="borda-exterior">
        <div className="borda-inferior">
          <div className="tela">
            <Screen valores={{atual, anterior, operacao}}/>
          </div>
          <div className="botoes">
            <Buttons atualizarValor={atualizarValor}/>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
